<html> <head> <title></title> <?= HTML::script('assets.js.main') ?> <?= HTML::style('assets.css.main') ?> </head> <body> <div class="content">  <h1>Content</h1> <table> <thead> <tr> <th>username</th> <th>password</th> <th>posts</th> </tr> </thead> <tbody> <?php foreach($users as $user) : ?> <tr> <td><?= $user->username ?></td> <td><?= $user->password ?></td> <td> <?php foreach($user->posts as $post) : ?> <p><?= $post->title ?></p> <?php endforeach; ?> </td> </tr> <?php endforeach; ?> </tbody> </table>  </div> <div class="form"> <?= FORM::open('contact', 'contact/send', array('class' => 'small-form')) ?> <ul> <li> <?= FORM::label('Not Member?') ?> <?= FORM::checkbox('type', 'member', array('class' => 'member', 'id' => 'member'), true) ?> </li> <li> <?= FORM::label('Membership Type:') ?> <?= FORM::radio('type', array('basic','advanced','super'), array('class' => 'member', 'id' => 'member'), 'basic') ?> </li> <li> <?= FORM::label('Username') ?> <?= FORM::input('username', 'John', array('class' => 'username', 'id' => 'user')) ?> </li> <li> <?= FORM::label('Password') ?> <?= FORM::password('password') ?> </li> <li> <?= FORM::label('Message') ?> <?= FORM::text('message') ?> </li> <li> <?= FORM::dropdown('countries', array('us' => 'USA', 'ph' => 'Philippines', 'th' => 'Thailand'), null, 'ph') ?> </li> <li><?= FORM::submit('Send') ?></li> </ul> <?= FORM::close() ?> </div> </body> </html> 